import React, { createContext, useReducer } from "react";
import loginApi from "../api/loginApi";

import { Usuario, LoginRresponse, LoginData } from '../interfaces/UsuarioInterface';
import { authReducer, AuthState } from "./AuthReducer";

type AutchContextProps = {
    errorMessage: string;
    token: string|null;
    user: Usuario|null;
    status: 'cheking' | 'authenticated' | 'unauthenticated';
    signUp: () => void;
    signIn: (loginData: LoginData) => void;
    logOut: () => void;
    removeError: () => void;
}

const AuthInitialState: AuthState = {
    status: 'cheking',
    errorMessage: '',
    token: null,
    user: null
}

export const AuthContext = createContext({} as AutchContextProps);

export const AuthProvider = ({ children }: any) => {

    const [state, dispatch] = useReducer(authReducer, AuthInitialState)

    const signIn = async({email, password}: LoginData) => {
        try {
            const resp = await loginApi.post<LoginRresponse>('/login', {
                email: '',
                password: '',  
            });
            console.log(resp)
        } catch (error) {
            console.log(error);
        }
    };

    const signUp = () => {};
    
    const logOut = () => {};
    const removeError = () => {};

    return(
        <AuthContext.Provider value = {{
            ...state,
            signUp,
            signIn,
            logOut,
            removeError,
        }}>
            {children}
        </AuthContext.Provider>
    )
}
