import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({
    globalMargin:{
        marginHorizontal: 20,
    },
    title:{
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 10,
    },
    botonGrande:{
        backgroundColor: '#00a680',
        padding: 10,
        borderRadius: 5,
        marginBottom: 10,
        marginRight: 10,
    },
    botonGrandeTexto:{
        color: 'white',
        fontSize: 20,
        fontWeight: 'bold',
    },
    Logo:{
        width: 100,
        height: 100,
        borderRadius: 50,
        marginBottom: 10,
    },
    avatarContainer:{  
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 20,
    },
    menuContainer:{
        marginBottom: 20,
        marginVertical: 20,
        marginHorizontal: 20,
        marginTop: 20,
        borderRadius: 5,
        height: 500,
        alignItems: 'center',
        backgroundColor: 'white',
    },
    menuTexto:{
        fontSize: 20,
        fontWeight: 'bold',
        color: 'white',
    },
    menuBotones:{
        marginTop: 50,
        marginBottom: 50,
        backgroundColor: '#6788a8',
        borderRadius: 20,
        height: 52,
        alignItems: 'center',
        justifyContent: 'center',
        borderColor: 'white',
        borderWidth: 1,
        paddingHorizontal: 20,
        paddingVertical: 10,
    },
    imagePoster:{
        width: '100%',
        height: 700,
    },
    channelData:{
        marginBottom: 20,
    },
    feedData:{
        marginBottom: 20,
    },
});

export const loginStyles = StyleSheet.create({
    container: {
        flex: 1,

        justifyContent: 'center',
        paddingHorizontal: 30,
        height: 600,
        marginBottom: 50
    },
    title: {
        fontSize: 30,
        fontWeight: 'bold',
        marginBottom: 20,
        color: 'white',
    },
    logo: {
        width: 100,
        height: 100,
        borderRadius: 50,
        marginBottom: 10,
    },
    logoImg: {
        width: '100%',
        height: '100%',
        borderRadius: 50,
    },
    avatarContainer: {
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 20,
    },
    avatar: {
        width: 100,
        height: 100,
        borderRadius: 50,
    },
    form: {
        marginBottom: 20,
        marginHorizontal: 20,
    },
    label: {
        fontSize: 18,
        marginBottom: 25,
        color: 'white',
    },
    input: {
        borderBottomColor: 'white',
        borderBottomWidth: StyleSheet.hairlineWidth,
        height: 40,
        fontSize: 15,
        color: '#161F3D',
        marginBottom: 20,
        paddingBottom: 4
        
    },
    inputIOS: {
        borderBottomColor: 'white',
        borderBottomWidth: StyleSheet.hairlineWidth,
        height: 20,
        fontSize: 15,
        color: '#161F3D',
    },
    button: {
        marginHorizontal: 20,
        backgroundColor: '#6788a8',
        borderRadius: 100,
        height: 52,
        alignItems: 'center',
        justifyContent: 'center',
        borderColor: 'white',
        borderWidth: 1,
        paddingHorizontal: 20,
        paddingVertical: 10,
    },
    buttonContainer: {
        marginTop: 20,
        alignItems: 'center',
    },
    buttonText: {
        fontWeight: 'bold',
        fontSize: 16,
        color: '#fff',
    },
});

export const registerStyles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        paddingHorizontal: 30,
        height: 600,
        marginBottom: 50,
    },
    title: {
        fontSize: 30,
        fontWeight: 'bold',
        marginBottom: 20,
        color: '#6788a8',
    },
    logo: {
        width: 100,
        height: 100,
        borderRadius: 50,
        marginBottom: 10,
    },
    logoImg: {
        width: '100%',
        height: '100%',
        borderRadius: 50,
    },
    avatarContainer: {
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 20,
    },
    avatar: {
        width: 100,
        height: 100,
        borderRadius: 50,
    },
    form: {
        marginBottom: 20,
        marginHorizontal: 20,
    },
    label: {
        fontSize: 18,
        marginBottom: 25,
        color: '#6788a8',
    },
    input: {
        borderBottomColor: '#8A8F9E',
        borderBottomWidth: StyleSheet.hairlineWidth,
        height: 40,
        fontSize: 15,
        color: '#161F3D',
        marginBottom: 20,
        paddingBottom: 4
    },
    inputIOS: {
        borderBottomColor: '#8A8F9E',
        borderBottomWidth: StyleSheet.hairlineWidth,
        height: 20,
        fontSize: 15,
        color: '#161F3D',
    },
    button: {
        marginHorizontal: 20,
        backgroundColor: '#6788a8',
        borderRadius: 100,
        height: 52,
        alignItems: 'center',
        justifyContent: 'center',
        borderColor: 'white',
        borderWidth: 1,
        paddingHorizontal: 20,
        paddingVertical: 10,
    },
    buttonContainer: {
        marginTop: 20,
        alignItems: 'center',
    },
    buttonText: {
        fontWeight: 'bold',
        fontSize: 16,
        color: '#fff',
    },
    buttonReturn: {
        marginTop: 20,
        marginBottom: 20,
        backgroundColor: '#6788a8',
        borderRadius: 100,
        height: 52,
        alignItems: 'center',
        justifyContent: 'center',
        borderColor: 'white',
        borderWidth: 1,
        paddingHorizontal: 20,
        paddingVertical: 10,
    },
});

export const protectedStyles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        paddingHorizontal: 30,
        height: 600,
        marginBottom: 50,
    },
    title: {
        fontSize: 30,
        fontWeight: 'bold',
        marginBottom: 20,
        color: '#6788a8',
    },
    logo: {
        width: 100,
        height: 100,
        borderRadius: 50,
        marginBottom: 10,
    },
    logoImg: {
        width: '100%',
        height: '100%',
        borderRadius: 50,
    },
});
