import React from 'react'
import { Channel, Feed } from '../interfaces/ClimateInterface';
import { Alert, Text, View } from 'react-native';
import { styles } from '../theme/appTheme';
import { getDistance } from 'geolib';
import { useLocation } from '../hooks/useLocation';
import { useEffect } from 'react';


interface ClimateDataProps { 
    channel: Channel;
    feeds: Feed;
}

export const ClimateData = ({channel, feeds}: ClimateDataProps) => {

    const {location, errorMsg} = useLocation();
    
    let text = 'Waiting..';
    if (errorMsg) {
        text = errorMsg;
    } else if (location) {
        text = JSON.stringify(location);
    }

    const showAlert = async () => {
        try{
            await Alert.alert(
                'Costa Peligrosa',
                'Dirigase a un lugar seguro',
                [
                    {
                        text: 'Cancel',
                        onPress: () => console.log('Cancel Pressed'),
                        style: 'cancel',
                    },
                    { text: 'OK', onPress: () => console.log('OK Pressed') },
                ],
                { cancelable: false },
            );
        }catch(error){
            console.log(error);
        }
    }
    
    const calculateDistance = () => {
        var dis = getDistance(
            { latitude: channel.latitude, longitude: channel.longitude },
            { latitude: location.latitude , longitude: location.longitude }   
          );
        return dis/1000;
    }
    useEffect(() => {
        getLocation();
        setInterval(getLocation, 1000);
    }, []);


    if (parseFloat(feeds.field3)>=15 || parseFloat(feeds.field3)<=9) {
        showAlert();
    }

    return (
        <View style = {styles.globalMargin}> 
            <View style = {styles.channelData}>
                <Text style={styles.title}>{channel.description}</Text>
                <Text style={styles.title}>Distancia del equipo: {calculateDistance()} Km</Text>
            </View>
            <View style = {styles.feedData}>
                <Text style={styles.title}>Latitud: {channel.latitude}</Text>
                <Text style={styles.title}>Longitud: {channel.longitude}</Text>
                

                <Text style={styles.feedDataText}>Temperatura: {feeds.field5}Â°C</Text>  
                <Text style={styles.feedDataText}>Humedad: {feeds.field6}%</Text> 

                <Text style={styles.feedDataText}>Nivel de Agua Maximo: {feeds.field1} Cm</Text> 
                <Text style={styles.feedDataText}>Nivel de Agua Minimo: {feeds.field2} Cm</Text>
                <Text style={styles.feedDataText}>Nivel de Agua Promedio: {feeds.field3} Cm</Text>

                <Text style={styles.feedDataText}>Vientos: {feeds.field4} Km/h</Text>

                <Text style={styles.feedDataText}>{text}</Text>

            </View>
        </View>
    )
}
