import React from 'react'
import { Keyboard, KeyboardAvoidingView, Platform, Text, View } from 'react-native';
import { TextInput, TouchableOpacity } from 'react-native-gesture-handler';
import { Backgound } from '../components/Backgound';
import { Logo } from '../components/Logo';
import { loginStyles } from '../theme/appTheme';
import { useForm } from '../hooks/useForm';
import { StackScreenProps } from '@react-navigation/stack';

interface Props extends StackScreenProps<any, any>{};

export const LoginScreen = ({ navigation }: Props ) => {

    const {email, password, onChange} = useForm ({
        email: '',
        password: '',
    });

    const onLoginPress = () => {
        /* navigation.navigate('MenuScreen'); */
        console.log({email, password});
        Keyboard.dismiss();
    }

    return (
        <>
            {/* Background */}
            <Backgound/>

            <KeyboardAvoidingView 
                style={loginStyles.container} 
                behavior={Platform.OS === 'ios' ? 'padding' : 'height'}>
                {/* Keyboard avoid view */}
                <Logo/>
                <Text style={ loginStyles.title }>Inicio de sesion</Text>

                <Text style={ loginStyles.label }>Email:</Text>
                <TextInput style={loginStyles.input} 
                    placeholder="Ingrese su email:" 
                    placeholderTextColor="white" 
                    underlineColorAndroid="white"
                    onChangeText={(text) => onChange(text, 'email')}
                    value={email}
                    onSubmitEditing={onLoginPress}
                    autoCapitalize="none"
                    autoCorrect={false}
                />
                <Text style={ loginStyles.label }>Contrase√±a:</Text>
                <TextInput 
                    style={loginStyles.input} 
                    placeholder="******" 
                    placeholderTextColor="white" 
                    underlineColorAndroid="white"
                    secureTextEntry={true}
                    onChangeText={(text) => onChange(text, 'password')}
                    value={password}
                    onSubmitEditing={onLoginPress}
                    autoCapitalize="none"
                    autoCorrect={false}
                />
                <View style={loginStyles.buttonContainer}>
                    <TouchableOpacity 
                        style={loginStyles.button} 
                        onPress={() => navigation.navigate('MenuScreen')}
                    >
                        <Text style={loginStyles.buttonText}>Iniciar Sesion</Text>
                    </TouchableOpacity>
                </View>
                <View style={loginStyles.buttonContainer}>
                    <TouchableOpacity 
                        style={loginStyles.button} 
                        onPress={() => navigation.navigate('RegisterScreen') }
                    >
                        <Text style={loginStyles.buttonText}>Nueva Cuenta</Text>
                    </TouchableOpacity>
                </View>
                
            </KeyboardAvoidingView>

        </>
    )
}
